INSERT INTO `product` VALUES (44, 7, 2, 'Smartheart - Pate vị bò cho chó lớn 130gr', 'PETPF32', 'smartheart-pate-vi-bo-cho-cho-lon-130gr', 'storage/ProductAvatar/3448_pate_smartheart_cho_cho_lon.jpg', '\n\n                <h2>\nPate Smartheart lon vị  bò </a>cho chó lớn 130gr</h2>\r\n<h2><strong>Thông tin sản phẩm:</strong></h2>\r\n<p><strong>Pate Smartheart  vị bò  cho chó lớn 130gr</strong> là sản phẩm được nghiên cứu sản xuất để phù hợp với nhu cầu dinh dưỡng của chú cún cưng nhà bạn. Với đầy đủ các dưỡng chất thiết yếu cùng với hương vị thịt bò và thịt cừu đầy hấp dẫn, Thức Ăn Cho Chó <strong>Pate Smartheart  vị bò  cho chó 130gr</strong> chắc hẳn sẽ là lựa chọn hàng đầu giúp cún cưng của bạn phát triển khỏe mạnh.</p>\r\n<p>Sản phẩm chứa đầy đủ các dưỡng chất thiết yếu, hỗ trợ cho quá trình hoạt động và phát triển của thú cưng nhà bạn, bao gồm:</p>\r\n<p>- Chất đạm: được chọn từ thịt có chất lượng, cung cấp acid amin cần thiết để xây dựng cơ bắp khỏe mạnh.</p>\r\n<p>- Chất béo: cung cấp năng lượng và nguồn acid béo thiết yếu để duy trì một làn da khỏe mạnh và một bộ lông bóng mượt.</p>\r\n<p>- Vitamin: giúp cơ thể phát triển khỏe mạnh và tăng cường hệ thống miễn dịch.</p>\r\n<p>- Khoáng chất: giúp cơ thể phát triển khỏe mạnh, xương chắc và răng khỏe. </p>\r\n<p><strong>Hướng dẫn sử dụng:</strong></p>\r\n<p>- Có thể cho cún con dùng trực tiếp hoặc trộn chung với các loại rau, củ, quả, hạt tùy thích.</p>\r\n<p>- Bảo quản ở nhiệt độ dưới 4 độ C sau khi mở lon và sử dụng hết trong vòng tối đa là 3 ngày.</p>\r\n<p>- Bảo quản ở nơi khô ráo, thoáng mát, tránh nơi ẩm ướt và tránh ánh sáng trực tiếp.</p>\n\n            ', 18000, '2020-04-30 14:56:42', 19000, 1, 5);
