INSERT INTO `product` VALUES (69, 8, 16, 'Pate Ciao 4 thanh / túi 14g', 'PETMYSP1737', 'pate-ciao-4-thanh-tui-14g', 'storage/ProductAvatar/4500_15b91d98cbe65319e79dd0f40a7cf482.jpg', '\n\n                <p><span style=\"color: #ff0000;\"><strong><span style=\"font-family: arial, helvetica, sans-serif; font-size: 14pt;\">Pate Ciao 4 thanh / túi 14g</span></strong></span></p>\r\n<p><span style=\"font-size: 12pt;\"><strong>Thành phần</strong></span></p>\r\n<p><span style=\"font-size: 12pt;\">Cá ngừ, sò điệp, tinh bột biến tính, chất tạo mùi, Guar Gum, chiết xuất sò điệp, Vitamin E, Carrageenan, bột trà xanh, Fructooligosaccharides. Có nhiều hương vị khác nhau </span></p>\r\n<p><span style=\"font-size: 12pt;\"><strong>Thành phần dinh dưỡng </strong></span></p>\r\n<p><span style=\"font-size: 12pt;\">Độ ẩm ≤ 93,0 %; Protein thô ≥ 7,0 %; Béo thô ≥ 0,2 %; Xơ thô ≤ 1,0 %; Khoáng tổng số ≤ 2,0 %</span></p>\r\n<p><span style=\"font-size: 12pt;\"><strong>Sử dụng</strong></span></p>\r\n<p><span style=\"font-size: 12pt;\">Cho ăn trực tiếp – Dùng như bánh thưởng.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Khẩu phần: 56g/ ngày.</span></p>\r\n<p><span style=\"font-size: 12pt;\">Sản phẩm này không dùng thay thế bữa ăn chính. Luôn giữ cung cấp nước sạch thường xuyên.</span></p>\r\n<p style=\"text-align: center;\"><img src=\"/media/product/4500_15b91d98cbe65319e79dd0f40a7cf482.jpg\" alt=\"\"></p>\r\n<p style=\"text-align: center;\"> </p>\n\n            ', 33000, '2020-04-30 14:54:30', 35000, 1, 6);
INSERT INTO `product` VALUES (70, 8, 18, 'Cỏ bạc hà thành phẩm', 'PETMYSP1192', 'co-bac-ha-thanh-pham', 'storage/ProductAvatar/4497_d51ad0a36650970ece41.jpg', '\n\n                <p><strong><span style=\"font-size: 12pt;\">Cỏ bạc hà thành phẩm</a></span></strong></p>\r\n<p style=\"text-align: left;\" align=\"center\"><span style=\"font-size: 12pt;\">Cỏ mèo chứa nhiều chất xơ, giúp cho mèo tiêu hóa thịt cũng như là xương. </span></p>\r\n<p style=\"text-align: left;\" align=\"center\"><span style=\"font-size: 12pt;\">Cỏ bạc hà mèo còn chứa rất nhiều vitamin và khoáng chất, cân bằng dinh dưỡng cho mèo. Giúp mèo khạc lông. Như chúng ta đều biết, mèo cưng mỗi ngày đều phải liếm lông để vệ sinh cơ thể, và vô tình trung đã nuốt phải rất nhiều lông, khi lông tích tụ ở dạ dày và ruột sẽ làm giảm thể tích dạ dày, tắc ruột… rất có hại, cỏ mèo có tác dụng kích thích mèo khạc lông ra ngoài, làm hạn chế các bệnh về đường tiêu hóa, như chướng hơi, sình bụng. và làm giảm stress cho mèo.</span></p>\r\n<p style=\"text-align: left;\" align=\"center\"><span style=\"font-size: 12pt;\"> Dùng thường xuyên cỏ mèo, có gây nghiện cho mèo cưng không? Điểm này bạn có thể yên tâm, theo các nghiên cứu của nước ngoài thì bạc hà mèo là loại thảo dược thiên nhiên không độc, sẽ không gây nghiện cho mèo cưng. </span></p>\r\n<p style=\"text-align: left;\" align=\"center\"><span style=\"font-size: 12pt;\">Tuy bạc hà mèo có tác dụng gây hưng phấn cho mèo, nhưng cũng chỉ là phản ứng tăng nhẹ nhịp tim, vả cảm giác bay bổng, cho nên sẽ có tác ổn định tậm trạng cho mèo. </span></p>\r\n<p style=\"text-align: center;\" align=\"center\"><span style=\"font-size: 12pt;\"><img src=\"/media/product/4497_2973d39042145b06c88ec83468ef433f_removebg_preview.png\" alt=\"\"></span></p>\n\n            ', 28000, '2020-04-30 14:56:22', 28000, 0, 0);
